<?php

namespace App\Http\Controllers;

use Illuminate\Http\Request;
use App\Models\User;
use App\Models\UserAccountModel;
use App\Models\WebPageSettingsModel;

use Hash;
use Session;
use Validator;
use DataTables;
use Illuminate\Support\Facades\Storage;

class LASWebpageController extends Controller
{
	
	/*Fetch Information*/
	public function web_settings_info(Request $request){

		$data = WebPageSettingsModel::first();
		return response()->json($data);
		
	}
	
	public function update_web_navigation_header_title_settings_post_OLD(Request $request){
		
			$request->validate([
				'navigation_header_title'    	=> 'required'
			], 
			[
				'navigation_header_title.required' => 'Navigation Header Title is required'
			]
			);
				
			$WebPageSettings = new WebPageSettingsModel();
			$WebPageSettings = WebPageSettingsModel::first();
			$WebPageSettings->navigation_header_title = $request->navigation_header_title;
			
			$result = $WebPageSettings->update();
			
			if($result){
				return response()->json(['success'=>'Navigation Header Title Successfully Updated!']);
			}
			else{
				return response()->json(['success'=>'Error on Update Navigation Header Title Information']);
			}
	
	}
	
	public function update_web_navigation_header_title_settings_post(Request $request){

			$default_web_settings = $request->default_web_settings;
			
			if($default_web_settings=='on'){
							
							/*Set to Default*/
							$WebPageSettings = new WebPageSettingsModel();
							$WebPageSettings = WebPageSettingsModel::first();
							$WebPageSettings->default_web_settings				 	= 1;
							$WebPageSettings->navigation_header_title 	= 'Lighting Automation System';
							$WebPageSettings->header_navigation_width 	= '70';
							$WebPageSettings->login_page_logo_width 	= '100';
	/*Default Image is DEC*/$WebPageSettings->image_logo 				= "";
							$result = $WebPageSettings->update();
				
			}else{
					
			   if ($request->hasFile('image_logo')) {
			
						$request->validate([
							'image_logo'							=>'image|mimes:jpg,png,jpeg|max:10048',
							'navigation_header_title'    			=> 'required',
							'header_navigation_width'      			=> 'required',
							'login_page_logo_width'    				=> 'required',
						],[
							'navigation_header_title.required' 		=> 'Navigation Header Title is required',
							'header_navigation_width.required' 		=> 'Header Logo Width is Required',
							'login_page_logo_width.required' 		=> 'Login Logo Width is Required',
						]);
		     
					   $path = 'files/';
					   $file = $request->file('image_logo');
					   @$file_name = time().'_'.@$file->getClientOriginalName();

						$upload = $file->storeAs($path, $file_name, 'public');

						$path = $request->file('image_logo')->getRealPath();    
						$logo = file_get_contents($path);
						$image_blob = base64_encode($logo);
							
							$WebPageSettings = new WebPageSettingsModel();
							$WebPageSettings = WebPageSettingsModel::first();
							$WebPageSettings->default_web_settings				 	= 0;
							$WebPageSettings->navigation_header_title 	= $request->navigation_header_title;
							$WebPageSettings->header_navigation_width 	= $request->header_navigation_width;
							$WebPageSettings->login_page_logo_width 	= $request->login_page_logo_width;
							$WebPageSettings->image_logo 				= $image_blob;
							$result = $WebPageSettings->update();

			   }else{	
				
							$WebPageSettings = new WebPageSettingsModel();
							$WebPageSettings = WebPageSettingsModel::first();
							$WebPageSettings->default_web_settings				 	= 0;
							$WebPageSettings->navigation_header_title	= $request->navigation_header_title;
							$WebPageSettings->header_navigation_width 	= $request->header_navigation_width;
							$WebPageSettings->login_page_logo_width 	= $request->login_page_logo_width;
	/*Default Image is DEC*/$WebPageSettings->image_logo 				= "";
							$result = $WebPageSettings->update();
						
			   }
			   
			}
					
						if($result){
							return response()->json(array('success' => "Successfully Saved!"), 200);
						}
						else{
							return response()->json(['success'=>'Error on Web Settings Information']);
						}	

           }
     
	
}
